import React from 'react';
import {connect} from "react-redux"


const ShowUser=(props) => {

  function handleClickOnUser(event){
    event.preventDefault()
    props.showRepos()
    props.selectUser(props.login)
    console.log(props)
  }
  return(<li onClick={handleClickOnUser}>
      {props.user.name}
      </li>
  )
}


function mapDispatchToProps(dispatch) {
  return {
    showRepos: () => {
      dispatch({type: "SHOW_REPO"})
    },
    selectUser: (data)=>{
      dispatch({type: "SELECT_USER", payload: data})
    }
  }
}


export default connect(null,mapDispatchToProps)(ShowUser)
