import React from 'react';
import {connect} from "react-redux";



const SearchUser =(props) => {

  function handleLoginSearch(event){
      event.preventDefault()
      console.log("username",props.userNameInput)
      const username=encodeURIComponent(steven.trim())
      const repos = encodeURIComponent(steven.trim())

      const url=`https://api.github.com/search/users?q=${props.userNameInput}+repos:%3E${props.numberOfReposInput}+followers:%3E${props.numberOfFollowersInput}+location:%22Hong%20Kong%22`
  }

  return(<div>
          <form onSubmit={handleLoginSearch}>
          <label>Name:
              <input type="text" value={props.userNameInput} onChange={(event) => props.handleUserNameInput(event)} placeholder="enter searchname" required/>
          </label>
          <label>Number of Repos:
              <input type="number" value={props.numberOfReposInput} onChange={(event) => props.handleReposInput(event)} placeholder="enter number of repos" required/>
          </label>
          <label>location:
              <input type="text" value={props.locationInput} onChange={(event) => props.handleLocationInput(event)} placeholder="enter location" required/>
          </label>
          <label>number of followers:
              <input type="number" value={props.numberOfFollowersInput} onChange={(event) => props.handleFollowersInput(event)} placeholder="enter number of followers" required/>
          </label>
              <input type="submit" value="Search"/>
          </form>
          <div>Can't find what you want? try advanced search</div>
      </div>
  )
}

function mapStateToProps(state) {
  return {userNameInput: state.userNameInput,
          numberOfReposInput: state.numberOfReposInput,
          locationInput: state.locationInput,
          numberOfFollowersInput: state.numberOfFollowersInput,
        }
      }

function mapDispatchToProps(dispatch) {
  return {
    handleUserNameInput: (event) => {
      dispatch({type: "UPDATE_USER_NAME_INPUT", payload: event.target.value})
    },
    handleReposInput: (event) => {
      dispatch({type: "UPDATE_REPOS_INPUT", payload: event.target.value})
    },
    handleLocationInput: (event) => {
      dispatch({type: "UPDATE_LOCATION_INPUT", payload: event.target.value})
    },
    handleFollowersInput: (event) => {
      dispatch({type: "UPDATE_FOLLOWERS_INPUT", payload: event.target.value})
    },
}
}

export default connect(mapStateToProps,mapDispatchToProps)(SearchUser)
