import React, { Component, Fragment } from 'react';
import './App.css';
import {connect} from "react-redux"
import SearchUser from "./SearchUser"
import UserList from "./UserList"
import RepoList from "./RepoList"

class App extends Component {

  getUserData=(userlist)=>{
    if (!!userlist.message) {
        console.log(userlist.message)
        props.handleUserListFetchError()
        props.handleUserListFetchError(userlist.message)
    }else {
      props.noUserListFetchError()
      return userlist.forEach(user=>{
        fetch(user.url).then(r=>r.json()).then(data=>this.props.fetchUsers(data)).catch(data=>console.log(data))
      })
    }

  }

  componentDidMount(){
    const url = "https://api.github.com/users?since=0"
    fetch(url).then(r=>r.json()).then(userlist=>this.getUserData(userlist))
  }



  render() {
    return (
      <Fragment>
        <SearchUser/>
        {this.props.showRepos ?
          <div className="middle">
            <div className="half"><UserList/></div>
            <div className="half"><RepoList/></div>
          </div>

        :
        <div className="middle">
        <UserList/>
        </div>
      }

      </Fragment>
    );
  }
}

function mapStateToProps(state) {
  return {showRepos: state.showRepos,
          userListFetchMessage: state.userListFetchMessage,
  }
}

function mapDispatchToProps(dispatch) {
  return {
    fetchUsers: (data) => {
      dispatch({type: "GET_USERS", payload: data})
    },
    handleUserListFetchError:(data) =>{
      dispatch({type:"FETCHING_ERROR",payload:data})
    },
    handleUserListFetchError:() =>{
      dispatch({type:"USER_LIST_FETCH_ERROR"})
    },
    noUserListFetchError:() =>{
      dispatch({type:"NO_USER_LIST_FETCH_ERROR"})
    },

  }
}

export default connect(mapStateToProps,mapDispatchToProps)(App);
