import React from 'react';
import {connect} from "react-redux"


const ShowUser=(props) => {



  function handleClickOnUser(event){
    event.preventDefault()
    props.showRepos()
    props.selectUser(props.user.login)
    // console.log(props.user.login)

    // "https://api.github.com/users/steven0608/repos?page=1&per_page=100"
    const url = `https://api.github.com/users/${props.user.login}/repos?page=1&per_page=100`
    // console.log(url)
    fetch(url).then(r=>r.json()).then(data=>props.getRepos(data))
  }
  return(<li onClick={handleClickOnUser}>
      {props.user.name}
      </li>
  )
}


function mapDispatchToProps(dispatch) {
  return {
    showRepos: () => {
      dispatch({type: "SHOW_REPO"})
    },
    selectUser: (data)=>{
      dispatch({type: "SELECT_USER", payload: data})
    },
    getRepos: (data)=>{
      dispatch({type: "GET_REPOS", payload: data})
    },
  }
}


export default connect(null,mapDispatchToProps)(ShowUser)
