import React from 'react';
import {connect} from "react-redux"


const ShowUser=(props) => {

  function handleClickOnUser(event){
    event.preventDefault()
    props.showRepos()
    props.selectUser(props.user.login)
    // console.log(props.user.login)

    // "https://api.github.com/users/steven0608/repos?page=1&per_page=100"
    const url = `https://api.github.com/users/${props.user.login}/repos?page=1&per_page=100`
    // console.log(url)
    fetch(url).then(r=>r.json()).then(data=>props.getRepos(data))
  }

  return(<div className="small card" onClick={handleClickOnUser}>
            <div className="image">
              <img src={props.user.avatar_url} alt="No profile Picture" />
            </div>

            <div class="content">
              <div class="header">{props.user.name}</div>
              <div class="meta">
                <a>Friends</a>
              </div>
              <div class="description">
                Matthew is an interior designer living in New York.
              </div>
            </div>
            <div class="extra content">
              <span class="right floated">
                Joined in 2013
              </span>
              <span>
                <i class="user icon"></i>
                75 Followers
              </span>
            </div>


      </div>
  )
}
// <img src={props.user.avatar_url} alt="No profile Picture">
//


function mapDispatchToProps(dispatch) {
  return {
    showRepos: () => {
      dispatch({type: "SHOW_REPO"})
    },
    selectUser: (data)=>{
      dispatch({type: "SELECT_USER", payload: data})
    },
    getRepos: (data)=>{
      dispatch({type: "GET_REPOS", payload: data})
    },
  }
}


export default connect(null,mapDispatchToProps)(ShowUser)
