import React,{Fragment} from 'react';
import {connect} from "react-redux";
import ShowUser from "./ShowUser"
import UUID from "uuid"



const UserList =(props) => {


    function handleUserslist(data) {
        data.forEach(user=>{
        fetch(user.url).then(r=>r.json()).then(data=>props.fetchUsers(data))
      })
    }

    function handleNextUserPageClick() {
      const nextPage = props.currentUserPage +1
      const nextNum = (nextPage - 1)* 30
      console.log(nextNum)
      console.log("next Page", nextPage)
      const url = `https://api.github.com/users?since=${nextNum}`
      fetch(url).then(r=>r.json()).then(data=>handleUserslist(data))
      props.updateUserCurrentPage(nextPage)
    }

    function handlePrevUserPageClick() {
        if (props.currentUserPage >1) {
          const prevPage = props.currentUserPage -1
          const nextNum = (prevPage-1)*30
          const url = `https://api.github.com/users?since=${nextNum}`
          fetch(url).then(r=>r.json()).then(data=>handleUserslist(data))
          props.updateUserCurrentPage(prevPage)
        }
    }


  return(<Fragment>
    <div className="ui six doubling cards">
      {props.userList.map(user=>
        <ShowUser user={user} key={UUID()}/>
      )}
      </div>
      <div>
      {props.currentUserPage === 1 ?
        <div><button className="ui right labeled icon button" onClick={handleNextUserPageClick}>Next<i className="right chevron icon"></i></button></div>
        :
          props.endOfRepos ?
          <div><button className="ui labeled icon button" onClick={handlePrevUserPageClick}><i className="left chevron icon"></i>Prev</button></div>
          :
        <div><button className="ui labeled icon button" onClick={handlePrevUserPageClick}><i className="left chevron icon"></i>Prev</button><button className="ui right labeled icon button" onClick={handleNextUserPageClick}>Next<i className="right chevron icon"></i></button></div>
      }
      </div>
      </Fragment>
  )
}

function mapStateToProps(state) {
  return {userList: state.userList,
          currentUserPage:state.currentUserPage,
          }
        }

function mapDispatchToProps(dispatch) {

  return {
          updateUserCurrentPage: (data) => {
            dispatch({type: "UPDATE_USER_CURRENT_PAGE",payload:data})
        },
        fetchUsers: (data) => {
          dispatch({type: "GET_USERS", payload: data})
        }
      }
  }


export default connect(mapStateToProps,mapDispatchToProps)(UserList)
