const defaultState = {
  userList:[],
  showRepos:false,
  selectedUser: "",
  reposList:[],

}

export default function(state = defaultState, action) {

  switch (action.type) {
    case "GET_USERS":
      return {
        ...state,
        userList: [...state.userList,
          action.payload
        ]
      }
      case "SHOW_REPO":
        return {
          ...state,
          showRepos: true
        }
      case "SELECT_USER":
        return {
          ...state,
          selectedUser:
            action.payload
        }
      case "GET_REPOS":
        return {
          ...state,
          reposList:
          action.payload
      }

    default:
      return state
  }
}
