import React, {Fragment }from 'react';
import {connect} from "react-redux";
import UUID from "uuid"
import ShowRepo from "./ShowRepo"



const RepoList =(props) => {

  function handleNextPageClick(){
    const nextPage = props.currentRepoPage + 1
    const url = `https://api.github.com/users/${props.selectedUser.login}/repos?page=${nextPage}&per_page=20`
    fetch(url).then(r=>r.json()).then(data=>props.updateRepoList(data))
    props.updateCurrentRepopage(nextPage)
  }

  function handlePrevPageClick(){
    if (props.currentRepoPage>1) {
      const prevPage = props.currentRepoPage - 1
      const url = `https://api.github.com/users/${props.selectedUser.login}/repos?page=${prevPage}&per_page=20`
      fetch(url).then(r=>r.json()).then(data=>props.updateRepoList(data))
      props.updateCurrentRepopage(prevPage)
    }
  }
  return(
    <Fragment>
      <center className="black">repositories List</center>
        <div className="ui list">
          {props.reposList.map(repo=>
            <ShowRepo repo={repo} key={UUID()}/>
            )}
          </div>
        <div>
        {props.currentRepoPage === 1 ?
          <div><button onClick={handleNextPageClick}>Next</button></div>
          :
          <div><button onClick={handlePrevPageClick}>Prev</button><button onClick={handleNextPageClick}>Next</button></div>
        }
        </div>
      </Fragment>
  )
}

function mapStateToProps(state) {
  return {reposList: state.reposList,
          selectedUser: state.selectedUser,
          currentRepoPage:state.currentRepoPage,

  }
}

function mapDispatchToProps(dispatch) {
  return {
    updateCurrentRepopage: (data) => {
      dispatch({type: "UPDATE_CURRENT_REPO_PAGE", payload: data})
    },
    updateRepoList: (data) => {
      dispatch({type: "UPDATE_REPO_LIST", payload: data})
    },

  }
}

export default connect(mapStateToProps,mapDispatchToProps)(RepoList)
