import React, { Component, Fragment } from 'react';
import './App.css';
import {connect} from "react-redux"
import SearchUser from "./SearchUser"
import UserList from "./UserList"
import RepoList from "./RepoList"

class App extends Component {

  getUserData=(userlist)=>{
    userlist.map(user=>{
      fetch(user.url).then(r=>r.json()).then(data=>this.fetchUsers)
    })
  }

  componentDidMount(){
    const url = "https://api.github.com/users?since=0"
    fetch(url).then(r=>r.json()).then(userlist=>this.getUserData(userlist))
  }



  render() {
    return (
      <Fragment>
        <h2>Welcome to Github Directory</h2>
        <SearchUser/>
        <UserList/>
        <RepoList/>
      </Fragment>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    fetchUsers: (data) => {
      dispatch({type: "GET_USERS", payload: data})
    }
  }
}

export default connect(mapDispatchToProps)(App);
